mapper.sql:
where 1 = 1

	and id = #{id}


	and train_nick = #{trainNick}


	and status = #{status}


	and description like concat('%', #{description}, '%')


	and id in

		#{item}

mapper.update:
update train 
set gmtModified = now()

	, set train_nick = #{trainNick}


	, set status = #{status}


	, set description = #{description}

where id = #{id}
